/**
 * @module indicative-rules
 */
/**
 * Tells whether input is empty or not
 *
 * @example
 * ```
 * const { is } = require('indicative')
 *
 *  // Following yield to true
 *  is.empty({})
 *  is.empty([])
 *  is.empty('')
 *  is.empty(null)
 *  is.empty(undefined)
 * ```
 */
export declare const empty: (input: any) => boolean;
